class Solution:
    def canPermutePalindrome(self, s: str) -> bool:
        d = {}
        for c in s:
            if c in d:
                d[c] = (d[c] + 1) % 2
            else:
                d[c] = 1
        return sum([x for x in d.values()]) <= 1


s = ""
print(Solution().canPermutePalindrome(s))